package com.example.myapplication.adapterimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.example.myapplication.ui.AllExpertsActivityimport com.example.myapplication.model.CategoriesModelimport com.example.myapplication.Rclass CategoriesAdapter(val context: Context, var datalist: ArrayList<CategoriesModel>) : RecyclerView.Adapter<CategoriesAdapter.MyViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        var view = LayoutInflater.from(parent.context).inflate(R.layout.rv_categories, parent, false)        return MyViewHolder(view)    }    override fun getItemCount(): Int {        return datalist.size    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        holder.categoriesPic.setImageResource(datalist.get(position).categoriespic)        holder.categoriesName.text = datalist[position].categoriesname        holder.itemView.setOnClickListener {            val intent = Intent(context, AllExpertsActivity :: class.java)            intent.putExtra("category name",datalist[position].categoriesname)            context.startActivity(intent)        }    }    class MyViewHolder(view: View) : RecyclerView.ViewHolder(view) {        val categoriesPic = view.findViewById<ImageView>(R.id.iv_categoriespic)        val categoriesName = view.findViewById<TextView>(R.id.tv_categoriesname)    }}